CssStorage.EventsCSS:
.newspaper{
    font-family: 'Segoe UI';
    font-size: 0.9em;
    padding: 0 4px;
}
.newspaper p{
    line-height: 140%;
    margin-top: 6px;
}

.newspaper a{
    color:#4169e1;
    text-decoration:none;
}
.newspaper a:hover{
    color:#00f;
    text-decoration:underline;
}
.newspaper ol,
.newspaper ul{
    margin: 4px 0;
}
.newspaper li p{
    margin: 8px 0;
}
.newspaper .bold{
    font-weight:600
}
.newspaper.italic{
    font-style: italic;
}
.newspaper .bigger-18{
	font-size: 18px;
}
.newspaper .bigger-24{
	font-size: 24px;
}
.newspaper .bigger-32{
	font-size: 32px;
}
.newspaper img.rage{
    width:40px;
    vertical-align:middle;
    margin:0 2px;
}

.newspaper h4,
.newspaper h5{
    margin-top:4px;
    margin-bottom: 2px;
}
.newspaper h5{
	font-size: 1em;
}

.newspaper .clear{
    clear: both;
}
.newspaper .right{
    float:right;
}
.newspaper .left{
    float:left;
}
.newspaper .auto{
	display: inline-block;
}
.newspaper figure{
    border:1px solid #f5f5f5;
    padding:4px;
    border-radius:2px;
    margin: 2px 8px;
}
.newspaper figcaption{
    text-align: center;
}
.newspaper figure img{
    margin:4px 0;
    max-width: 800px;
    max-height: 600px;
}
.newspaper caption{
    font-size:.8em;
    text-align:left;
    font-style:italic;
}

.newspaper blockquote{
    margin-left:1.5em;
    border-left:2px solid orange;
    padding-left:8px
}
.newspaper blockquote p{
    margin:4px 0
}
.newspaper blockquote.user{
    border-color:#32cd32
}
.newspaper blockquote.post{
    border-color:orange
}
.newspaper blockquote.comment{
    border-color:#4169e1
}
.newspaper .mini-ava{
	max-height: 20px;
	max-width: 20px;
}
.newspaper cite{
    font-size:1em;
    font-weight: 500;
}
.newspaper .user-cite-content{
	display: flex;
	align-items: center;
}
.newspaper .center{
	text-align: center;
}

