.quote-user-content,
.creation-description {
    font-family: Verdana, Geneva, Tahoma, sans-serif;
    font-size: 13px;
}
.quote-user-content img.rage,
.creation-description img.rage{
    width:40px;
    vertical-align:middle;
    margin:0 2px;
}

.quote-user-content p,
.creation-description p{
    line-height: 140%;
    margin-top: 6px;
}

.quote-user-content a,
.creation-description a{
    color:#4169e1;
    text-decoration:none;
}
.quote-user-content a:hover,
.creation-description a:hover{
    color:#00f;
    text-decoration:underline;
}

.quote-user-content .comment,
.creation-description .comment{
	color: gray;
	background-color: whitesmoke;
	padding: 4px;
	border: 1px solid gainsboro;
}
.quote-user-content .quote-quote{
	border: 1px solid gainsboro;
	padding: 8px 4px;
	background-color: #fbfbeb;
}
.quote-user-content .quote-quote-author{
	border: 1px solid gainsboro;
	padding: 4px;
	background-color: #f7f5d3;
	font-weight: 600;
	color: rgb(64,64,64);
}
